import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { useHistory } from 'react-router-dom';
import { useForm } from 'react-hook-form'
import Swal from 'sweetalert2'

// function AddProduct({ onAdd }) {
//     let history = useHistory();
//     const [valueInput, setValueInput] = useState({});
//     function onHandleChange(e) {
//         const { name, image, price, desc, value } = e.target;
//         setValueInput({
//             ...valueInput,
//             [name]: e.target.value,
//             [image]: e.target.value,
//             [price]: e.target.value,
//             [desc]: e.target.value,
//         })
//     }

//     function onSubmit() {
//         onAdd(valueInput);
//         history.push("/admin/products");
//     }

//     const { register, handleSubmit, watch, errors } = useForm();
const AddProduct = ({ onAdd }) => {
    const { register, handleSubmit, errors } = useForm();
    let history = useHistory();
    const onSubmit = (data) => {
        Swal.fire(
            'Created!',
            'Your file has been created.',
            'success'
        )
        onAdd(data);
        history.push("/admin/products");
    };
    return (
        <div>
            <section class="content">
                <h3 className="card-title">Thêm sản phẩm</h3>
                <form >
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Tên sản phẩm</label>
                        <input type="text"
                            name="name"
                            className="form-control"
                            id="inputProductName"
                            aria-describedby="emailHelp"
                            ref={register({ required: true })} />
                        {errors.name && errors.name.type === "required" && <span style={{ color: "red" }}>Tên sản phẩm không được để trống</span>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Gía sản phẩm</label>
                        <input type="number"
                            name="price"
                            className="form-control"
                            id="inputProductName"
                            aria-describedby="emailHelp"
                            ref={register({ required: true, min: 0 })} />
                        {errors.price && errors.price.type === "required" && <span style={{ color: "red" }}>Gía sản phẩm không được để trống</span>}
                        {errors.price && errors.price.type === "min" && <span style={{ color: "red" }}>Gía sản phẩm phải lớn hơn không</span>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Ảnh sản phẩm</label>
                        <input type="text"
                            name="image"
                            className="form-control"
                            id="inputProductName"
                            aria-describedby="emailHelp"
                            ref={register({ required: true })} />
                        {errors.image && <span style={{ color: "red" }}>Ảnh sản phẩm không được để trống</span>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Mô tả sản phẩm</label>
                        <textarea className="form-control"
                            name="desc" id="inputProductName"

                            ref={register({ required: true })} />
                        {errors.desc && <span style={{ color: "red" }}>Mô tả sản phẩm không được để trống</span>}
                    </div>
                    <button onClick={handleSubmit(onSubmit)} type="submit" className="btn btn-primary">Submit</button>
                </form>
            </section>
        </div>
    )
}

export default AddProduct;
