import http from "./axiosHttp";
// list product
const getAll = () => {
    return http.get("/products");
};
// remove product
const remove = id => {
    console.log(id);
    return http.delete(`/products/${id}`);
};
// add product
const create = data => {
    return http.post("/products", data);
};
export default { getAll, remove, create };
