import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { useHistory, useParams } from 'react-router-dom'

const EditProduct = ({ products, onEdit }) => {
    let history = useHistory();
    let { id } = useParams();
    const product = products.find(product => product.id == id);
    const [valueInput, setValueInput] = useState({});
    function onHandleChange(e) {
        const { name, image, price, Desc, value } = e.target;
        setValueInput({
            ...valueInput,
            [name]: e.target.value,
            [image]: e.target.value,
            [price]: e.target.value,
            [Desc]: e.target.value,
        })
    }
    function onSubmit() {
        onEdit(valueInput);
        history.push("/admin/products");
    }
    return (
        <div>
            <section class="content">
                <h3 className="card-title">Sửa sản phẩm</h3>
                <form >
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Tên sản phẩm</label>
                        <input type="text"
                            name="name"
                            className="form-control"
                            id="inputProductName"
                            aria-describedby="emailHelp"
                            defaultValue={product.name}
                            onChange={onHandleChange} />
                    </div>
                    {/* <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Gía sản phẩm</label>
                        <input type="number"
                            name="price"
                            className="form-control"
                            id="inputProductName"
                            aria-describedby="emailHelp"
                            defaultValue={product.price} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Ảnh sản phẩm</label>
                        <input type="text"
                            name="image"
                            className="form-control"
                            id="inputProductName"
                            aria-describedby="emailHelp"
                            defaultValue={product.image} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Mô tả sản phẩm</label>
                        <textarea className="form-control"
                            name="Desc" id="inputProductName"
                            defaultValue={product.Desc}
                        />
                    </div> */}
                    <button onClick={onSubmit} type="submit" className="btn btn-primary">Submit</button>
                </form>
            </section>

        </div>
    )
}

EditProduct.propTypes = {

}

export default EditProduct
