import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { useParams, useHistory } from 'react-router-dom';
import { useForm } from "react-hook-form";
import Swal from 'sweetalert2'
import { Editor } from '@tinymce/tinymce-react';
import firebase from '../../../../firebase'

const EditPost = ({ posts, onUpdatePost }) => {
    let { id } = useParams();
    let history = useHistory();
    let post = posts.find((data) => data.id == id);
    const [desc_post, setDesc] = useState("");
    const { register, handleSubmit, errors } = useForm();
    // const onHandleSubmit = (e) => {
    //     Swal.fire(
    //         'Updated!',
    //         'Your file has been updated.',
    //         'success'
    //     )
    //     onUpdatePost(currentPost);
    //     history.push("/admin/posts");
    // };
    // const onHandleChange = (e) => {
    //     const { name, value } = e.target;
    //     setCurrentPost({
    //         ...currentPost,
    //         [name]: value,
    //         desc_post,
    //     });
    // };
    // const handleEditorChange = (content, editor) => {
    //     setDesc(content);
    // }
    // const { register, handleSubmit, errors } = useForm();
    const onHandleSubmit = (data) => {
        // Swal.fire(
        //     'Created!',
        //     'Your file has been created.',
        //     'success'
        // )
        let file = data.image_post[0];
        let storageRef = firebase.storage().ref(`image_post/${file.name}`);
        storageRef.put(file).then(function () {
            storageRef.getDownloadURL().then((url) => {
                // console.log(url);

                const newData = {
                    ...data,
                    desc_post,
                    image_post: url
                };
                console.log(newData);
                onUpdatePost(newData);
                // history.push("/admin/posts");
            })
        });
    };
    const handleEditorChange = (content, editor) => {
        setDesc(content);
    }
    return (
        <div>
            <form action="" onSubmit={handleSubmit(onHandleSubmit)} className="w-100" >
                <div className="form-group">
                    <label htmlFor="productName">Tiêu đề</label>
                    <input type="text" name="title" defaultValue={post.title} className="form-control" ref={register({ required: true })} />
                    {errors.title && <span style={{ color: "red" }}>Tên sản phẩm không được để trống</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="productPrice">Ảnh bài viết</label>
                    <div className="input-group">
                        <div className="custom-file">
                            <input type="file"
                                className="custom-file-input"
                                id="inputGroupFile02"
                                name="image_post"
                                ref={register}
                            />
                            <label className="custom-file-label" htmlFor="inputGroupFile02" aria-describedby="imageHelp">Choose image</label>
                        </div>
                    </div>
                </div>
                <div className="form-group">
                    <label htmlFor="productName">Mô tả bài viết</label>
                    <input type="text" name="short_desc" defaultValue={post.short_desc} className="form-control" ref={register({ required: true })} />
                    {errors.short_desc && <span style={{ color: "red" }}>Tên sản phẩm không được để trống</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="productDescription">Nội dung bài viết</label>
                    <Editor
                        init={{
                            height: 500,
                            images_upload_url: 'postAcceptor.php',
                            plugins: [
                                'advlist autolink lists link image charmap print preview anchor',
                                'searchreplace visualblocks code fullscreen',
                                'insertdatetime media table paste code help wordcount'
                            ],
                            toolbar:
                                'undo redo | formatselect | bold italic backcolor |  image link\
                                alignleft aligncenter alignright alignjustify | \
                                bullist numlist outdent indent | removeformat | help',

                        }}
                        onEditorChange={handleEditorChange}
                    />
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

EditPost.propTypes = {

}

export default EditPost
