import React from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'
import Swal from 'sweetalert2'
const Categories = ({ categories, onRemoveCate, products }) => {
    // let product = products.find((data) => data.id == id);
    // console.log(product);
    const product = products.find((product) => product.cateId == categories.id);
    const removeHandle = (id) => {
        Swal.fire({
            title: 'Bạn có chắc chắn muốn xóa?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.value) {
                onRemoveCate(id)
                Swal.fire(
                    'Deleted!',
                    'Your file has been deleted.',
                    'success'
                )
            }
        })
        console.log(id)
    }
    return (
        <div>
            <div className="card shadow mb-4">
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table">
                            <thead>
                                <tr>
                                    <th scope="col">#</th>
                                    <th scope="col">Name</th>
                                    <th scope="col">Total product</th>
                                    <th scope="col">Action</th>
                                </tr>
                            </thead>
                            <tbody>
                                {categories.map((category, index) => (
                                    <tr key={index}>
                                        <th scope="row">{index + 1}</th>
                                        <td><Link to={`/admin/detail-category/${category.id}`}>{category.name_category}</Link></td>
                                        <td>{products.map((product) => product.cateId == category.id && <p>{product.name}</p>)}</td>
                                        <td>
                                            <button className="btn btn-danger" onClick={() => removeHandle(category.id)}>Xóa</button>
                                            <Link className='btn btn-primary ml-2' to={`/admin/edit-category/${category.id}`}>Sửa</Link>

                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>

                    </div>
                </div>
            </div>
        </div>
    )
}

Categories.propTypes = {

}

export default Categories
