import React, { useState } from 'react';

import dataFake from './dataFake';
import Routers from './routers'
import { Router } from 'react-router-dom';

function App() {

  const [products, setProducts] = useState(dataFake);

  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProducts(newProducts);
  }
  const onHandleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product
    }
    setProducts([
      ...products,
      newProduct
    ])
  }
  const onHandleEdit = (id, value_edit) => {
    const newProducts = [...products];
    newProducts.forEach((element, index) => {
      if (element.id == id) newProducts.splice(index, 1);
    });
    setProducts([...newProducts, value_edit]);
  };
  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} onAdd={onHandleAdd} onUpdate={onHandleEdit} />

    </div>
  )

}
export default App;
