import http from "./axiosHttp";
// list cate
const getAllCate = () => {
    return http.get("/categories");
};
// remove cate
const removeCate = id => {
    console.log(id);
    return http.delete(`/categories/${id}`);
};
const createCate = data => {
    return http.post("/categories", data);
};
export default { getAllCate, removeCate, createCate };
