import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { useHistory, useParams } from 'react-router-dom'

const EditProduct = ({ products, onEdit }) => {
    let history = useHistory();
    let { id } = useParams();
    const [currentProduct, setCurrentProduct] = useState(product);

    const onHandleSubmit = (e) => {
        e.preventDefault();
        onUpdate(currentProduct);
        history.push('/admin/products');
    }
    const onHandleChange = e => {
        const { name, value } = e.target;
        setCurrentProduct({
            ...currentProduct,
            [name]: value
        })
    }
    return (
        <div>
            {products.map((product) => {
                if (product.id == id) {
                    return (
                        <div >
                            <section class="content">
                                <h3 className="card-title">Sửa sản phẩm</h3>
                                <form onSubmit={onHandleSubmit}>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Tên sản phẩm</label>
                                        <input type="text"
                                            name="name"
                                            className="form-control"
                                            id="inputProductName"
                                            aria-describedby="emailHelp"
                                            defaultValue={product.name}
                                            onChange={onHandleChange}
                                        />

                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Gía sản phẩm</label>
                                        <input type="number"
                                            name="price"
                                            className="form-control"
                                            id="inputProductName"
                                            aria-describedby="emailHelp"
                                            defaultValue={product.price}
                                            onChange={onHandleChange} />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Ảnh sản phẩm</label>
                                        <input type="text"
                                            name="image"
                                            className="form-control"
                                            id="inputProductName"
                                            aria-describedby="emailHelp"
                                            defaultValue={product.image}
                                            onChange={onHandleChange} />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="exampleInputEmail1">Mô tả sản phẩm</label>
                                        <textarea className="form-control"
                                            name="desc" id="inputProductName"
                                            defaultValue={product.desc}
                                            onChange={onHandleChange} />
                                    </div>
                                    <button type="submit" className="btn btn-primary">Submit</button>
                                </form>
                            </section>
                        </div>
                    );
                }
            })}


        </div>
    )
}

EditProduct.propTypes = {

}

export default EditProduct
